import React, { useState, useCallback } from "react";

/**
 * Renders an array of strings passed in that can be filtered and added to as an
 * unordered list.
 * @returns Component
 */
export default function Sidebar({ initialMenuItems }) {
  // State for new menu item input
  let [newMenuItem, setNewMenuItem] = useState("");

  // TODO: 2 - Create state for menu items
  // Initialize the state using initialMenuItems
  let [menuItems, setMenuItems] = useState(initialMenuItems);

  // State for filter
  let [filter, setFilter] = useState("");

  // Adds a new menu item
  let addMenuItem = useCallback(() => {
    if (newMenuItem.trim()) {
      // Add the new item to the state array
      setMenuItems([newMenuItem, ...menuItems]);
      setNewMenuItem(""); // Clear the input field after adding
    }
  }, [newMenuItem, menuItems]);

  return (
    <div>
      <ul>
        {menuItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>

      <input
        type="text"
        id="newMenuItemValue"
        value={newMenuItem}
        onChange={(event) => setNewMenuItem(event.target.value)}
      />
      <br />
      <button onClick={addMenuItem}>Add Item</button>
      <br />
      <input
        id="filter"
        type="text"
        value={filter}
        onChange={(event) => setFilter(event.target.value)}
        placeholder="Filter by..."
      />
    </div>
  );
}
